package comparisonOperatoer;

public class nestedNoob {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 4;
		if((num % 2) == 0)
		{
			if(num == 2)
			{
				System.out.println("hi");
			}
			else
			{
				System.out.println("bie");
			}
				
	
		}
		else
		{
			if(num % 2 != 0)
			{
				System.out.println("bye");
			}
			
		}

	}

}
