package comparisonOperatoer;

public class primeNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 22;
		if(a==2) {
			System.out.println("it's prime number ");
		}else
		{
			if(a%2 ==0) {
				System.out.println("it's not prime number ");
				
			}else
			{
				if(a%2 !=0) 
				{
					System.out.println("it's prime number ");
				}
			}
		}

	}

}
