package dsabasis;

public class bitwise2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 9;
		int b = 12;
		int c = a & b;
		System.out.println("a & b = " + c);
		c = a | b;
		System.out.println("a | b = " + c);
		c = a ^ b;
		System.out.println("a ^ b = " + c);
		c = ~a;
		System.out.println("~a = " + c);
	}

}
