package javaBasic;

public class calculateAreaOfCircle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//input radius and calculate area of circle and circumference of a circle. Area of circle = 3.14*r*r, Circumference of circle = 2*3.14*r
		
		int radius = 10;
		int areaOfCircle = (int)3.14*radius*radius;
		int circumferenceOfCircle = (int) (2*3.14*radius);
		System.out.println(areaOfCircle);
		System.out.println(circumferenceOfCircle);

	}

}
