package javaBasic;

public class volume {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		//input radius and height of a cylinder and calculate volume of cylinder. Volume=3.14*r*r*h
		
		int radius = 10;
		int height = 20;
		int volume = (int) 3.14*radius*radius*height;
		System.out.println(volume);

	}

}
